import React, { useState, useEffect } from 'react';
import './PlayerStats.css';

const PlayerStats = () => {
    const [stats, setStats] = useState(null);  // State to store player and team stats
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const playerStats = JSON.parse(localStorage.getItem('playerStats'));

        if (!playerStats) {
            setError('Player stats not found. Please log in again.');
            setLoading(false);
            return;
        }

        setStats(playerStats);
        setLoading(false); // Set loading to false once stats are fetched
    }, []);

    if (loading) {
        return <div>Loading stats...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    return (
        <div>
            <h2>Player and Team Stats</h2>
            <div>
                <h3>{stats.Player_Name} (Player)</h3>
                <p><strong>Team:</strong> {stats.Team_name}</p>
                <p><strong>Sport:</strong> {stats.Sport_Name}</p>
                <p><strong>Wins:</strong> {stats.Wins}</p>
                <p><strong>Points Scored:</strong> {stats.Points_scored}</p>
                <p><strong>Goals Scored:</strong> {stats.Goals_scored}</p>
                <p><strong>Stat Type:</strong> {stats.Stat_Type}</p>
            </div>
        </div>
    );
};

export default PlayerStats;
