// client/src/components/PlayerDashboard.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate from react-router-dom
import './PlayerDashboard.css';

const PlayerDashboard = () => {
  const navigate = useNavigate(); 
  const [isProfileModalOpen, setIsProfileModalOpen] = useState(false);
  const [userData, setUserData] = useState({
    username: 'JohnDoe',  // Dynamically set from login
    email: 'johndoe@gmail.com',
    playerId: 123  // Add playerId here
  });

  // Function to handle "View Stats" button click
  const handleViewStatsClick = () => {
    navigate(`/playerstats/${userData.playerId}`); // Pass playerId instead of username
  };

  return (
    <div className="player-dashboard">
      {/* Navbar */}
      <div className="navbar">
        <h1 className="navbar-title">Sports Management System</h1>
        <div className="profile-icon" onClick={handleProfileClick}>
          <img src="https://via.placeholder.com/40" alt="Profile" className="profile-image" />
        </div>
      </div>

      {/* Main Content */}
      <div className="dashboard-content">
        <div className="welcome-message">
          <h2>Welcome back, Player!</h2>
          <p>View your stats, schedule, and more!</p>
        </div>

        {/* Dashboard Cards */}
        <div className="dashboard-cards">
          <div className="card">
            <h3>Stats And Performances</h3>
            <button className="action-button" onClick={handleViewStatsClick}>View Stats</button>
          </div>
        </div>
      </div>

      {/* Profile Modal */}
      {isProfileModalOpen && (
        <div className="profile-modal" onClick={handleBackgroundClick}>
          <div className="profile-modal-content">
            <span className="close-button" onClick={closeProfileModal}>&times;</span>
            <div className="profile-info">
              <h3>{userData.username}</h3>
              <p><strong>Email:</strong> {userData.email}</p>
            </div>
            <button className="logout-button" onClick={handleLogout}>Logout</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default PlayerDashboard;
