import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './TeamManagement.css';

const TeamManagement = () => {
  const [teamName, setTeamName] = useState('');
  const [teamSport, setTeamSport] = useState('');
  const [teamMembers, setTeamMembers] = useState([]);
  const [teamCoach, setTeamCoach] = useState('');
  const [newMember, setNewMember] = useState('');
  const [newPlayer, setNewPlayer] = useState({
    name: '',
    dob: '',
    gender: '',
    position: '',
    email: '',
    age: '',
    phone_no: ''
  });
  const [teamList, setTeamList] = useState([]);
  const [playerList, setPlayerList] = useState([]);
  const [selectedTeam, setSelectedTeam] = useState(null);

  // Fetch teams and players from the backend
  useEffect(() => {
    axios.get('/api/teams')
      .then(response => setTeamList(response.data))
      .catch(err => console.log('Error fetching teams:', err));

    axios.get('/api/players')
      .then(response => setPlayerList(response.data))
      .catch(err => console.log('Error fetching players:', err));
  }, []);

  const handleAddPlayer = (e) => {
    e.preventDefault();
    axios.post('/api/players', newPlayer)
      .then(response => {
        setPlayerList([...playerList, response.data]);
        setNewPlayer({
          name: '',
          dob: '',
          gender: '',
          position: '',
          email: '',
          age: '',
          phone_no: ''
        });
      })
      .catch(error => console.error("Error adding player:", error));
  };

  const handleAssignPlayer = (playerId) => {
    if (!selectedTeam) {
      alert('Please select a team');
      return;
    }

    axios.post('/api/players/assign-player', {
      playerId,
      teamId: selectedTeam.Team_ID
    })
    .then(response => {
      alert('Player assigned to team');
    })
    .catch(err => console.error('Error assigning player:', err));
  };

  const handleSubmitTeam = (e) => {
    e.preventDefault();
    if (!teamName || !teamCoach || !teamSport || teamMembers.length === 0) {
      alert('Please fill in all fields and add at least one member');
      return;
    }
    axios.post('/api/teams', { name: teamName, sport: teamSport, coach: teamCoach })
      .then(response => {
        setTeamList([...teamList, response.data]);
        setTeamName('');
        setTeamSport('');
        setTeamCoach('');
        setTeamMembers([]);
      })
      .catch(error => console.error('Error adding team:', error));
  };

  return (
    <div className="team-management">
      <h1>Team Management</h1>

      <div className="box">
        <h3>Add Player</h3>
        <form onSubmit={handleAddPlayer}>
          <input type="text" placeholder="Player Name" value={newPlayer.name} onChange={(e) => setNewPlayer({ ...newPlayer, name: e.target.value })} />
          <input type="date" placeholder="DOB" value={newPlayer.dob} onChange={(e) => setNewPlayer({ ...newPlayer, dob: e.target.value })} />
          <input type="text" placeholder="Position" value={newPlayer.position} onChange={(e) => setNewPlayer({ ...newPlayer, position: e.target.value })} />
          <input type="text" placeholder="Email" value={newPlayer.email} onChange={(e) => setNewPlayer({ ...newPlayer, email: e.target.value })} />
          <input type="text" placeholder="Age" value={newPlayer.age} onChange={(e) => setNewPlayer({ ...newPlayer, age: e.target.value })} />
          <input type="text" placeholder="Phone No." value={newPlayer.phone_no} onChange={(e) => setNewPlayer({ ...newPlayer, phone_no: e.target.value })} />
          <button type="submit">Add Player</button>
        </form>
      </div>

      <div className="box">
        <h3>Teams</h3>
        <select onChange={(e) => setSelectedTeam(teamList.find(team => team.Team_ID == e.target.value))}>
          <option value="">Select Team</option>
          {teamList.map(team => (
            <option key={team.Team_ID} value={team.Team_ID}>{team.Team_Name}</option>
          ))}
        </select>

        <h4>Assign Player to Team</h4>
        <ul>
          {playerList.map(player => (
            <li key={player.Player_ID}>
              {player.Player_Name} <button onClick={() => handleAssignPlayer(player.Player_ID)}>Assign</button>
            </li>
          ))}
        </ul>
      </div>

      <div className="box">
        <h3>Add New Team</h3>
        <form onSubmit={handleSubmitTeam}>
          <input type="text" placeholder="Team Name" value={teamName} onChange={(e) => setTeamName(e.target.value)} />
          <input type="text" placeholder="Sport" value={teamSport} onChange={(e) => setTeamSport(e.target.value)} />
          <input type="text" placeholder="Coach" value={teamCoach} onChange={(e) => setTeamCoach(e.target.value)} />
          <button type="submit">Add Team</button>
        </form>
      </div>
    </div>
  );
};

export default TeamManagement;
