// ScheduleView.js (Player Dashboard - View All Scheduled Matches)
import React, { useState, useEffect } from 'react';
import './ScheduleView.css'; // You can customize the CSS to style this page

const ScheduleView = () => {
    const [matches, setMatches] = useState([]);
    const [errorMessage, setErrorMessage] = useState('');

    // Fetch scheduled matches from the backend when the component mounts
    useEffect(() => {
        const fetchAllSchedules = async () => {
            try {
                const response = await fetch('http://localhost:5001/api/allschedules');
                const result = await response.json();

                if (response.status === 200) {
                    setMatches(result.matches); // Store the list of matches
                } else {
                    setErrorMessage(result.message || 'Error fetching scheduled matches.');
                }
            } catch (error) {
                setErrorMessage('Error fetching scheduled matches. Please try again.');
            }
        };

        fetchAllSchedules();
    }, []);

    return (
        <div className="schedule-view-container">
            <h2>All Scheduled Matches</h2>
            {errorMessage && <p className="error-message">{errorMessage}</p>}

            {matches.length > 0 ? (
                <div className="match-list">
                    {matches.map((match) => (
                        <div key={match.Match_ID} className="match-card">
                            <h3>{match.Team_A} vs {match.Team_B}</h3>
                            <p><strong>Sport:</strong> {match.Sport_name}</p>
                            <p><strong>Date:</strong> {match.Date_of_match}</p>
                            <p><strong>Location:</strong> {match.Location}</p>
                            <p><strong>Score:</strong> {match.Score ? match.Score : 'TBD'}</p>
                        </div>
                    ))}
                </div>
            ) : (
                <p>No scheduled matches at the moment.</p>
            )}
        </div>
    );
};

export default ScheduleView;
