import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom'; // Import useParams to get URL parameters
import './PlayerStats.css';

const PlayerStats = () => {
  const { playerId } = useParams(); // Get playerId from the URL params
  const [playerStats, setPlayerStats] = useState([]);
  const [teamStats, setTeamStats] = useState([]);
  const [showTeamStats, setShowTeamStats] = useState(false);

  useEffect(() => {
    if (!playerId) {
      console.error("Player ID is undefined or null");
      return; // Early return if playerId is invalid
    }

    // Fetch player stats when the component loads
    axios.get(`http://localhost:5001/api/playerStats/${playerId}`)
      .then(response => {
        setPlayerStats(response.data);
      })
      .catch(error => {
        console.error('Error fetching player stats:', error);
      });
  }, [playerId]); // This will re-fetch the data if playerId changes

  // Fetch team stats when the "View Team Stats" button is clicked
  const handleViewTeamStats = () => {
    if (playerStats[0]?.Team_ID) {
      axios.get(`http://localhost:5001/api/teamStats/${playerStats[0]?.Team_ID}`)
        .then(response => {
          setTeamStats(response.data);
          setShowTeamStats(true);
        })
        .catch(error => {
          console.error('Error fetching team stats:', error);
        });
    } else {
      console.error("Player's team ID is missing");
    }
  };

  return (
    <div className="player-stats">
      <h2>Player Stats</h2>
      <table>
        <thead>
          <tr>
            <th>Sport</th>
            <th>Wins</th>
            <th>Points Scored</th>
            <th>Goals Scored</th>
          </tr>
        </thead>
        <tbody>
          {playerStats.map((stat, index) => (
            <tr key={index}>
              <td>{stat.Sport_Name}</td>
              <td>{stat.Wins}</td>
              <td>{stat.Points_scored}</td>
              <td>{stat.Goals_scored}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Button to show team stats */}
      <button onClick={handleViewTeamStats}>View Team Stats</button>

      {showTeamStats && (
        <div>
          <h3>Team Stats</h3>
          <table>
            <thead>
              <tr>
                <th>Sport</th>
                <th>Wins</th>
                <th>Points Scored</th>
                <th>Goals Scored</th>
              </tr>
            </thead>
            <tbody>
              {teamStats.map((stat, index) => (
                <tr key={index}>
                  <td>{stat.Sport_Name}</td>
                  <td>{stat.Wins}</td>
                  <td>{stat.Points_scored}</td>
                  <td>{stat.Goals_scored}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default PlayerStats;
