// src/administrator/PlayerDetailsUpdate.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const PlayerDetailsUpdate = ({ playerId }) => {
    const [playerDetails, setPlayerDetails] = useState({
        Player_Name: '',
        Position: '',
        Profile_Picture: '',
        Bio: '',
    });

    const [errorMessage, setErrorMessage] = useState('');
    const [successMessage, setSuccessMessage] = useState('');

    // Fetch existing player details
    useEffect(() => {
        const fetchPlayerDetails = async () => {
            try {
                const response = await axios.get(`http://localhost:5001/api/player/${playerId}`);
                setPlayerDetails(response.data);
            } catch (error) {
                setErrorMessage('Error fetching player details.');
            }
        };
        fetchPlayerDetails();
    }, [playerId]);

    // Handle input changes
    const handleChange = (e) => {
        const { name, value } = e.target;
        setPlayerDetails((prevDetails) => ({
            ...prevDetails,
            [name]: value,
        }));
    };

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.put(`http://localhost:5001/api/player/update/${playerId}`, playerDetails);
            if (response.status === 200) {
                setSuccessMessage('Player details updated successfully.');
                setErrorMessage('');
            }
        } catch (error) {
            setErrorMessage('Error updating player details.');
            setSuccessMessage('');
        }
    };

    return (
        <div className="update-container">
            <h2>Update Player Details</h2>
            {errorMessage && <p className="error-message">{errorMessage}</p>}
            {successMessage && <p className="success-message">{successMessage}</p>}
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    name="Player_Name"
                    placeholder="Player Name"
                    value={playerDetails.Player_Name}
                    onChange={handleChange}
                    required
                />
                <input
                    type="text"
                    name="Position"
                    placeholder="Position"
                    value={playerDetails.Position}
                    onChange={handleChange}
                    required
                />
                <textarea
                    name="Bio"
                    placeholder="Bio"
                    value={playerDetails.Bio}
                    onChange={handleChange}
                />
                <input
                    type="text"
                    name="Profile_Picture"
                    placeholder="Profile Picture URL"
                    value={playerDetails.Profile_Picture}
                    onChange={handleChange}
                />
                <button type="submit">Update Details</button>
            </form>
        </div>
    );
};

export default PlayerDetailsUpdate;
