import React, { useState, useEffect } from 'react';
import axios from 'axios';

const PlayerStats = () => {
    const [stats, setStats] = useState(null); // State to store player stats
    const [error, setError] = useState(null); // State to store any errors
    const [loading, setLoading] = useState(true); // State to manage loading status

    useEffect(() => {
        const playerId = localStorage.getItem('playerId'); // Retrieve playerId from localStorage

        if (!playerId) {
            setError('Player ID is missing. Please log in again.');
            setLoading(false);
            return;
        }

        // Fetch player stats from the backend
        axios.get(`http://localhost:5001/api/playerStats/${playerId}`)
            .then((response) => {
                setStats(response.data); // Set stats state on successful fetch
                setLoading(false); // Set loading to false once data is fetched
            })
            .catch((error) => {
                setError('Error fetching player stats: ' + error.message); // Set error state on failure
                setLoading(false); // Set loading to false in case of an error
            });
    }, []); // Empty dependency array means this effect runs only once when the component mounts

    // Display loading or error message if applicable
    if (loading) {
        return <div>Loading player stats...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    return (
        <div>
            <h2>Player Stats</h2>
            <div>
                <p><strong>Player Name:</strong> {stats.Player_Name}</p>
                <p><strong>Team:</strong> {stats.Team_name}</p>
                <p><strong>Sport:</strong> {stats.Sport_Name}</p>
                <p><strong>Wins:</strong> {stats.Wins}</p>
                <p><strong>Points Scored:</strong> {stats.Points_scored}</p>
                <p><strong>Goals Scored:</strong> {stats.Goals_scored}</p>
                <p><strong>Stat Type:</strong> {stats.Stat_Type}</p>
            </div>
        </div>
    );
};

export default PlayerStats;
