// src/components/CreateTeamForm.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CreateTeamForm = () => {
  const [teamName, setTeamName] = useState('');
  const [maxTeamSize, setMaxTeamSize] = useState('');
  const [sportId, setSportId] = useState('');
  const [players, setPlayers] = useState([]);
  const [selectedPlayers, setSelectedPlayers] = useState([]);
  const [sports, setSports] = useState([]);

  // Fetch available sports and players
  useEffect(() => {
    axios.get('/api/sports')  // Replace with your API endpoint for fetching sports
      .then(res => setSports(res.data))
      .catch(err => console.error('Error fetching sports:', err));

    axios.get('/api/players/without-team')  // Replace with your API endpoint for fetching players without a team
      .then(res => setPlayers(res.data))
      .catch(err => console.error('Error fetching players:', err));
  }, []);

  const handlePlayerChange = (event) => {
    const value = Array.from(event.target.selectedOptions, option => option.value);
    setSelectedPlayers(value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const teamData = {
      Team_Name: teamName,
      Max_Team_Size: maxTeamSize,
      Sport_ID: sportId,
      Player_Ids: JSON.stringify(selectedPlayers),  // Converting selected players to JSON
    };

    axios.post('/api/create-team', teamData)
      .then(response => {
        console.log('Team Created:', response.data);
        alert('Team created successfully!');
      })
      .catch(error => {
        console.error('Error creating team:', error);
        alert('Error creating team');
      });
  };

  return (
    <div>
      <h1>Create Team</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Team Name:</label>
          <input 
            type="text" 
            value={teamName} 
            onChange={(e) => setTeamName(e.target.value)} 
            required
          />
        </div>

        <div>
          <label>Max Team Size:</label>
          <input 
            type="number" 
            value={maxTeamSize} 
            onChange={(e) => setMaxTeamSize(e.target.value)} 
            required 
          />
        </div>

        <div>
          <label>Sport:</label>
          <select 
            value={sportId} 
            onChange={(e) => setSportId(e.target.value)} 
            required
          >
            <option value="">Select Sport</option>
            {sports.map(sport => (
              <option key={sport.Sport_ID} value={sport.Sport_ID}>
                {sport.Sport_Name}
              </option>
            ))}
          </select>
        </div>

        <div>
          <label>Players:</label>
          <select 
            multiple 
            value={selectedPlayers} 
            onChange={handlePlayerChange} 
            required
          >
            {players.map(player => (
              <option key={player.Player_ID} value={player.Player_ID}>
                {player.Player_Name}
              </option>
            ))}
          </select>
        </div>

        <button type="submit">Create Team</button>
      </form>
    </div>
  );
};

export default CreateTeamForm;
