import React, { useState, useEffect } from 'react';
import './ScheduleManagement.css'; // Assuming you have a CSS file for styling

const ScheduleManagement = () => {
    const [teams, setTeams] = useState([]);
    const [teamA_ID, setTeamA_ID] = useState('');
    const [teamB_ID, setTeamB_ID] = useState('');
    const [date, setDate] = useState('');
    const [time, setTime] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const [successMessage, setSuccessMessage] = useState('');

    // Fetch teams from the backend
    useEffect(() => {
        const fetchTeams = async () => {
            try {
                const response = await fetch('http://localhost:5001/api/teams');
                const result = await response.json();
                setTeams(result);
            } catch (error) {
                setErrorMessage('Error fetching teams.');
            }
        };
        fetchTeams();
    }, []);

    const handleScheduleGame = async (e) => {
        e.preventDefault();

        if (!teamA_ID || !teamB_ID || !date || !time) {
            setErrorMessage('Please fill in all fields.');
            return;
        }

        const gameData = {
            teamA_ID,
            teamB_ID,
            date,
            time,
        };

        try {
            const response = await fetch('http://localhost:5001/api/schedulegame', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(gameData),
            });

            const result = await response.json();

            if (response.status === 200) {
                setSuccessMessage('Game scheduled successfully!');
                setErrorMessage('');
            } else {
                setErrorMessage(result.message);
                setSuccessMessage('');
            }
        } catch (error) {
            setErrorMessage('Error scheduling the game. Please try again.');
            setSuccessMessage('');
        }
    };

    return (
        <div className="schedule-management-container">
            <h2>Schedule a New Game</h2>
            {errorMessage && <p className="error-message">{errorMessage}</p>}
            {successMessage && <p className="success-message">{successMessage}</p>}

            <form onSubmit={handleScheduleGame} className="schedule-form">
                <div className="form-group">
                    <label>Team A:</label>
                    <select
                        value={teamA_ID}
                        onChange={(e) => setTeamA_ID(e.target.value)}
                        required
                        className="form-control"
                    >
                        <option value="">Select Team A</option>
                        {teams.map((team) => (
                            <option key={team.Team_ID} value={team.Team_ID}>
                                {team.Team_Name}
                            </option>
                        ))}
                    </select>
                </div>

                <div className="form-group">
                    <label>Team B:</label>
                    <select
                        value={teamB_ID}
                        onChange={(e) => setTeamB_ID(e.target.value)}
                        required
                        className="form-control"
                    >
                        <option value="">Select Team B</option>
                        {teams.map((team) => (
                            <option key={team.Team_ID} value={team.Team_ID}>
                                {team.Team_Name}
                            </option>
                        ))}
                    </select>
                </div>

                <div className="form-group">
                    <label>Date:</label>
                    <input
                        type="date"
                        value={date}
                        onChange={(e) => setDate(e.target.value)}
                        required
                        className="form-control"
                    />
                </div>

                <div className="form-group">
                    <label>Time:</label>
                    <input
                        type="time"
                        value={time}
                        onChange={(e) => setTime(e.target.value)}
                        required
                        className="form-control"
                    />
                </div>

                <button type="submit" className="btn btn-primary">Schedule Game</button>
            </form>
        </div>
    );
};

export default ScheduleManagement;
